import React, {useState, useEffect, useMemo} from 'react';

const App = () => {
    const [card, setCard] = useState({});
    const [cardName, setCardName] = useState('');
    const [cardSet, setCardSet] = useState('');
    const [options, setOptions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [blurred, setBlurred] = useState(true); // add a new state to keep track of the blur
    const [symbolUrl, setSymbolUrl] = useState(''); // new state variable to keep track of the symbol URL

    const fetchData = useMemo(() => {
        async function fetchData() {
            setLoading(true);
            setOptions([]);
            const response = await fetch("https://api.pokemontcg.io/v2/cards");
            const data = await response.json();
            const randomIndex = Math.floor(Math.random() * data.data.length);
            setCard(data.data[randomIndex]);
            setCardName(data.data[randomIndex].name);
            setCardSet(data.data[randomIndex].set.name);
            setSymbolUrl(data.data[randomIndex].set.images.symbol); // set the symbol URL when the card object is set
            setLoading(false);
            setBlurred(true); // reset the blur when loading a new card
        }
        return fetchData;
    }, []);

    const getRandomOptions = useMemo(() => {
        async function getRandomOptions() {
            if (!loading) {
                const response = await fetch("https://api.pokemontcg.io/v2/cards");
                const data = await response.json();
                let randomOptions = [];
                while (randomOptions.length < 4) {
                    const randomIndex = Math.floor(Math.random() * data.data.length);
                    if (randomOptions.indexOf(data.data[randomIndex].name) === -1) {
                        randomOptions.push(data.data[randomIndex].name);
                    }
                }
                randomOptions[Math.floor(Math.random() * 4)] = cardName;
                setOptions(randomOptions);
            }
        }
        return getRandomOptions;
    }, [loading, cardName]);

    useEffect(() => {
        if (cardName) {
            getRandomOptions();
        }
    }, [cardName, getRandomOptions]);

    const handleButtonPress = () => {
        fetchData();
    };

    const handleOptionClick = (option) => {
        if (option === cardName) {
            setBlurred(false); // remove blur when the correct option is clicked
        }
    };

    return (
        <div className="pokedex">
            <div className="left-container">
                <div className="left-container__top-section">
                    <div className="top-section__blue"></div>
                    <div className="top-section__small-buttons">
                        <div className="top-section__red"></div>
                        <div className="top-section__yellow"></div>
                        <div className="top-section__green"></div>
                    </div>
                    <div className='w-1/2 text-right text-xl font-bold text-red-900'>PACKADDX</div>
                </div>
                <div className="left-container__main-section-container">
                    <div className="left-container__main-section">
                        <div className="main-section__white">
                            {
                            loading ? <div className='object-contain h-full mx-auto text-center mt-32'>Loading...</div> : <img className={
                                    `object-contain h-full mx-auto ${
                                        blurred ? 'blurred-image' : ''
                                    }`
                                }
                                src={
                                    card.images && card.images.small
                                }
                                alt={
                                    card.name
                                }/>
                        }
                            <div className="main-section__black"></div>
                        </div>
                        <div className="left-container__controllers w-full h-20 flex mt-3">
      {/* render either an empty div or the symbol image */}
      <div className="w-1/5">
              {symbolUrl ? (
                <img src={symbolUrl} alt="symbol" />
              ) : (
                <div />
              )}
            </div>
                            <div className="w-full">
                            <div className='text-center text-sm'>GUESS THE SET BY THE SYMBOL!</div>
                            <div className='text-center flex justify-between m-5'>
                                <button>SECRET WONDERS</button>
                                <button>BASE SET 2</button>
                                <button>McDonalds Collections 2019</button>
                            </div>
                            </div>
                        </div>
                    </div>
                    <div className="left-container__right">
                        <div className="left-container__hinge"></div>
                        <div className="left-container__hinge"></div>
                    </div>
                </div>
            </div>
            <div className="right-container">
                <div className="right-container__black">
                    <div className="right-container__screen flex flex-col content-between space-y-6 text-xl">
                        {
                        !loading && options.length > 0 && options.map((option, index) => (
                            <button key={index}
                                className="pokemon-name"
                                onClick={
                                    () => handleOptionClick(option)
                            }>
                                {option}</button>
                        ))
                    } </div>
                </div>
                <div className="right-container__buttons flex flex-col mt-10p">
                    <button className="pokemon-name bg-green-400 mt-10"
                        onClick={handleButtonPress}>
                        LOAD NEW CARD
                    </button>
                </div>
            </div>
        </div>
    );
};

export default App;
