import React, { useState, useEffect } from 'react';

const App = () => {

  const [card, setCard] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("https://api.pokemontcg.io/v2/cards");
      const data = await response.json();
      const randomIndex = Math.floor(Math.random() * data.data.length);
      setCard(data.data[randomIndex]);
    };

    const handleButtonPress = () => {}
    fetchData();
  }, []);

  return (
    <div class="pokedex">
    <div class="left-container">
      <div class="left-container__top-section">
        <div class="top-section__blue"></div>
        <div class="top-section__small-buttons">
          <div class="top-section__red"></div>
          <div class="top-section__yellow"></div>
          <div class="top-section__green"></div>
        </div>
      </div>
      <div class="left-container__main-section-container">
        <div class="left-container__main-section">
          <div class="main-section__white">
          <img className='object-contain h-full mx-auto' src={card.images && card.images.small} alt={card.name} />
            <div class="main-section__black">

            </div>
          </div>
          <div class="left-container__controllers">
          <div className="bg-black w-5/6 h-9 mt-5 mx-auto text-yellow-500 text-center">
           CARD SET INFO GOES HERE
          </div>
          </div>
        </div>
        <div class="left-container__right">
          <div class="left-container__hinge"></div>
          <div class="left-container__hinge"></div>
        </div>
      </div>
    </div>
    <div class="right-container">
      <div class="right-container__black">
        <div class="right-container__screen flex flex-col content-between space-y-6 text-xl">
          <button class="pokemon-name">POKEMON</button>
          <button class="pokemon-name">POKEMON1</button>
          <button class="pokemon-name">POKEMON2</button>
          <button class="pokemon-name">POKEMON3</button>
        </div>
      </div>
      <div class="right-container__buttons flex flex-col mt-10p">
      <button class="pokemon-name bg-green-400" onClick={handleButtonPress}>LOAD NEW CARD</button>      </div>
    </div>
  </div>

  );
};

export default App;
