import React, { useState, useEffect } from 'react';
import logo from '../src/assets/title.png'

const App = () => {
  const [card, setCard] = useState({});
  const [loading, setLoading] = useState(false);
  const [blur, setBlur] = useState(true);
  const [randomNames, setRandomNames] = useState([]);


  useEffect(() => {
    setLoading(true);
    fetch('https://api.pokemontcg.io/v2/cards')
      .then(res => res.json())
      .then(data => {
        setCard(data.data[Math.floor(Math.random() * data.data.length)]);
        let names = [];
        while (names.length < 3) {
          let randomName = data.data[Math.floor(Math.random() * data.data.length)].name;
          if (!names.includes(randomName)) {
            names.push(randomName);
          }
        }
        setRandomNames(names);
        setLoading(false);
      });
  }, []);





  const removeBlur = () => setBlur(false);

  return (
    <div>
      {loading ? (
        <div className="wrapper">
          <div className="pokeball"></div>
        </div>
      ) : (
        <div className='grid justify-center items-center'>
          <div className='mb-10'>
          <img className='h-50' src={logo} alt="logo" />

          </div>
          <img className={`${blur ? 'backdrop-blur' : 'pointer-events-none'}`} src={card.images ? card.images.small : ''} alt={card.name} />
          <br />
          <div>
            <div>
              <span style={{ color: 'gray' }}>Set Name:</span> <span style={{ color: 'orange' }}>{card.set && card.set.name}</span>
            </div>
            <button type="button" class="py-2.5 px-5 mr-2 my-2 text-sm font-medium text-gray-900 focus:outline-none bg-white rounded-lg border border-gray-200 hover:bg-gray-100 hover:text-red-700 focus:z-10 focus:ring-4 focus:ring-gray-200 dark:focus:ring-gray-700 dark:bg-gray-800 dark:text-gray-400 dark:border-gray-600 dark:hover:text-white dark:hover:bg-gray-700" onClick={removeBlur}>{card.name}</button>
            {randomNames.map(randomName => (
              <button className='py-2.5 px-5 mr-2 my-2 text-sm font-medium text-gray-900 focus:outline-none bg-white rounded-lg border border-gray-200 hover:bg-gray-100 hover:text-blue-700 focus:z-10 focus:ring-4 focus:ring-gray-200 dark:focus:ring-gray-700 dark:bg-gray-800 dark:text-gray-400 dark:border-gray-600 dark:hover:text-white dark:hover:bg-gray-700' type="button">{randomName}</button>
            ))}
          </div>
          <div className='mt-5'>
            <button className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full' onClick={handleClick}>Load New Card</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
