import React, { useState, useEffect } from "react";
import axios from "axios";

const PokemonCard = () => {
  const [cardData, setCardData] = useState([]);
  const [correctName, setCorrectName] = useState("");
  const [selectedOption, setSelectedOption] = useState("");
  const [options, setOptions] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        "https://api.pokemontcg.io/v2/cards"
      );
      setCardData(result.data.cards);
    };
    fetchData();
  }, []);

  useEffect(() => {
    if (cardData.length > 0) {
      const randomIndex = Math.floor(Math.random() * cardData.length);
      setCorrectName(cardData[randomIndex].name);
      setOptions([
        cardData[randomIndex].name,
        cardData[(randomIndex + 1) % cardData.length].name,
        cardData[(randomIndex + 2) % cardData.length].name,
        cardData[(randomIndex + 3) % cardData.length].name,
      ]);
    }
  }, [cardData]);

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
  };

  return (
    <div>
      {cardData.length > 0 && (
        <>
          <img
            src={cardData[0].imageUrl}
            alt={correctName}
            style={{ width: "200px", height: "300px" }}
          />
          <div>
            {options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleOptionSelect(option)}
                disabled={selectedOption !== ""}
              >
                {option}
              </button>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default PokemonCard;
