import React, { useState, useEffect } from 'react';

const App = () => {
  const [card, setCard] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    fetch('https://api.pokemontcg.io/v2/cards')
      .then(res => res.json())
      .then(data => {
        setCard(data.data[Math.floor(Math.random() * data.data.length)]);
        setLoading(false);
      });
  }, []);

  const handleClick = () => {
    setLoading(true);
    fetch('https://api.pokemontcg.io/v2/cards')
      .then(res => res.json())
      .then(data => {
        setCard(data.data[Math.floor(Math.random() * data.data.length)]);
        setLoading(false);
      });
  };

  return (
    <div>
    <div className='grid justify-center items-center'>
      {loading ? (
        <div className="wrapper">
          <div className="pokeball" />
        </div>
      ) : (
        <>
          <div className='mb-6 text-white'>Set Name: {card.set && card.set.name}</div>
          <img src={card.images ? card.images.small : ''} alt={card.name} />
        </>
      )}
      <br />
      <button className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full' onClick={handleClick}>Load New Card</button>
    </div>
    </div>
  );
};

export default App;
