import React, { useState, useEffect } from 'react';

const App = () => {
  const [card, setCard] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    fetch('https://api.pokemontcg.io/v2/cards')
      .then(res => res.json())
      .then(data => {
        setCard(data.data[Math.floor(Math.random() * data.data.length)]);
        setLoading(false);
      });
  }, []);

  const handleClick = () => {
    setLoading(true);
    fetch('https://api.pokemontcg.io/v2/cards')
      .then(res => res.json())
      .then(data => {
        setCard(data.data[Math.floor(Math.random() * data.data.length)]);
        setLoading(false);
      });
  };

  return (
    <div className='flex justify-center'>
      {loading ? (
        <div className="wrapper">
          <div className="pokeball" />
        </div>
      ) : (
        <>
          <div>Set Name: {card.set && card.set.name}</div>
          <img src={card.images ? card.images.small : ''} alt={card.name} />
        </>
      )}
      <br />
      <button onClick={handleClick}>Load New Card</button>
    </div>
  );
};

export default App;
