import React, { useState, useEffect } from 'react';

const App = () => {
  const [card, setCard] = useState({});
  const [loading, setLoading] = useState(false);
  const [blur, setBlur] = useState(true);
  const [randomName, setRandomName] = useState('');
  const [namesArray, setNamesArray] = useState([]);

  useEffect(() => {
    setLoading(true);
    fetch('https://api.pokemontcg.io/v2/cards')
      .then(res => res.json())
      .then(data => {
        setNamesArray(data.data.map(item => item.name));
        setCard(data.data[Math.floor(Math.random() * data.data.length)]);
        setLoading(false);
      });
  }, []);

  const randomNameGenerator = () => {
    let randomName = namesArray[Math.floor(Math.random() * namesArray.length)];
    setRandomName(randomName);
    return randomName;
  };

  const handleClick = () => {
    setBlur(true);
    setLoading(true);
    fetch('https://api.pokemontcg.io/v2/cards')
      .then(res => res.json())
      .then(data => {
        setCard(data.data[Math.floor(Math.random() * data.data.length)]);
        setRandomName(data.data[Math.floor(Math.random() * data.data.length)].name);
        setLoading(false);
      });
  };

  const removeBlur = () => setBlur(false);

  return (
    <div>
      {loading ? (
        <div className="wrapper">
          <div className="pokeball" />
        </div>
      ) : (
        <div className='grid justify-center items-center'>
          <div className='mb-6 text-yellow-500'>
            <span style={{ color: 'gray' }}>Set Name:</span> {card.set && card.set.name}
          </div>
          <img className={`${blur ? 'backdrop-blur' : 'pointer-events-none'}`} src={card.images ? card.images.small : ''} alt={card.name} />
          <br />
          <div>
            <h2 className='text-white'>Who's That Pokemon?!</h2>
            <button type="button" class="py-2.5 px-5 mr-2 my-2 text-sm font-medium text-gray-900 focus:outline-none bg-white rounded-lg border border-gray-200 hover:bg-gray-100 hover:text-red-700 focus:z-10 focus:ring-4 focus:ring-gray-200 dark:focus:ring-gray-700 dark:bg-gray-800 dark:text-gray-400 dark:border-gray-600 dark:hover:text-white dark:hover:bg-gray-700" onClick={removeBlur}>{card.name}</button>
            <button type="button" >{randomNameGenerator()}</button>
            <button type="button" >{randomNameGenerator()}</button>
            <button type="button" >{randomNameGenerator()}</button>
          </div>
          <div className='mt-5'>
            <button className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full' onClick={handleClick}>Load New Card</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
