import React, { useState, useEffect } from 'react';

const App = () => {
  const [card, setCard] = useState({});

  useEffect(() => {
    fetch('https://api.pokemontcg.io/v2/cards')
      .then(res => res.json())
      .then(data => {
        setCard(data.data[Math.floor(Math.random() * data.data.length)]);
      });
  }, []);

  return (
    <div>
      <img src={card.images ? card.images.small : ''} alt={card.name} />
      <br />
      <button onClick={() => window.location.reload()}>Load New Card</button>
    </div>
  );
};

export default App;
