import React, { useState, useEffect } from "react";

const Card = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [card, setCard] = useState({});

  useEffect(() => {
    fetchCard();
  }, []);

  const fetchCard = async () => {
    setIsLoading(true);
    const response = await fetch("https://api.pokemontcg.io/v2/cards/random");
    const data = await response.json();
    setCard(data.card);
    setIsLoading(false);
  };

  return (
    <div>
      {isLoading ? (
        <div>
          <p>Loading...</p>
        </div>
      ) : (
        <img src={card.imageUrl} alt={card.name} />
      )}
      <button onClick={fetchCard}>Load new card</button>
    </div>
  );
};

export default Card;
