import React, { useState, useEffect } from 'react';
import { db } from '../firebase';

function Leaderboard() {
  const [scores, setScores] = useState([]);

  useEffect(() => {
    const scoresRef = db.collection('scores').orderBy('score', 'desc');
    scoresRef.onSnapshot((snapshot) => {
      const scoresData = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setScores(scoresData);
    });
  }, []);

  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Score</th>
          <th>Date</th>
        </tr>
      </thead>
      <tbody>
        {scores.map((score) => (
          <tr key={score.id}>
            <td>{score.name}</td>
            <td>{score.score}</td>
            <td>{score.date.toDate().toLocaleString()}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default Leaderboard;
