import React from 'react';

function SkipButton({ skipCount, label, onClick }) {


  return skipCount > 0 ? (
    <button onClick={onClick} className="mt-2 w-36 h-10 mx-auto bg-[#e71d23] cursor-pointer select-none
    active:translate-y-2  active:[box-shadow:0_0px_0_0_#e71d23,0_0px_0_0_#e71d23]
    active:border-b-[0px]
    transition-all duration-150 [box-shadow:0_7px_0_0_#c5151a]
    rounded-full  border-[1px] border-[#eb4146]">
     <span className='flex flex-col justify-center h-full text-black font-semibold text-lg'> 
        {label}
     </span>
    </button>
  ) : (
    <button className="bg-gray-400 text-white font-bold py-2 px-4 rounded cursor-not-allowed" disabled>
      Skips x0
    </button>
  );
}

export default SkipButton;
